import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const Products = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            const { data } = await axios.get("http://localhost:3001/products/allpr", {withCredentials: true});
            console.log(data);
            setProducts(data);
        };
        fetchProducts();
    }, []);

    return (
        <div>
            <h1>Products</h1>
            <div>

            {products.map((product) => (
                <div key={product.pr_id}>
                    <h1>{product.pr_name}</h1>
                    <p>{product.pr_stock}</p>
                </div>
            ))}
            </div>
            <div style={{display:"flex", flexDirection:"column"}}>
                <h2>Add Product</h2>
                <form>
                    <input type="text" placeholder="Product Name" />
                    <input type="number" placeholder="Product Stock" />
                    <input type="number" placeholder="Product Category" />
                    <button type="submit">Add Product</button>
                </form>
            </div>
        </div>
    );

};

export default Products;
