import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const Products = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            const { data } = await axios.get("http://localhost:3001/products/allpr", {withCredentials: true});
            console.log(data);
            setProducts(data);
        };
        fetchProducts();
    }, []);

    return (
        <div>
            <h1>Products</h1>
            {products.map((product) => (
                <div key={product.pr_id}>
                    <h1>{product.pr_name}</h1>
                    <p>{product.pr_stock}</p>
                </div>
            ))}
            <div>
                <h2>Add Product</h2>
                <form>
                    <label htmlFor="pr_name">Product Name</label>
                    <input type="text" id="pr_name" name="pr_name" />
                    <label htmlFor="pr_price">Product Price</label>
                    <input type="text" id="pr_price" name="pr_price" />
                    <label htmlFor="pr_stock">Product Stock</label>
                    <input type="text" id="pr_stock" name="pr_stock" />
                    <label htmlFor="pr_description">Product Description</label>
                    <input type="text" id="pr_description" name="pr_description" />
                    <label htmlFor="pr_image">Product Image</label>
                    <input type="text" id="pr_image" name="pr_image" />
                    <label htmlFor="pr_category">Product Category</label>
                    <input type="text" id="pr_category" name="pr_category" />
                    <button type="submit">Add Product</button>
                </form>
            </div>
        </div>
    );

};

export default Products;
