import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const Products = () => {
    const [allproducts, setAllProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [product, setProduct] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            const { data } = await axios.get("http://localhost:3001/products/allpr", {withCredentials: true});
            console.log(data);
            setAllProducts(data);
        };
        const fetchCategories = async () => {
            const { data } = await axios.get("http://localhost:3001/categories/", {withCredentials: true});
            console.log(data);
            setCategories(data);
        }
        fetchProducts();
        fetchCategories();
    }, []);

    const handleSetProduct = (e) => {
        setProduct(e.preventDefault());
        console.log(product);
    }

    const handleSubmit = async () => {
        console.log(product);
    }

    return (
        <div>
            <h1>Products</h1>
            <div style={{display:"flex", gap:"100px"}}>
                {allproducts.map((product) => (
                    <div key={product.pr_id}>
                        <h1>{product.pr_name}</h1>
                        <p>{product.pr_stock}</p>
                    </div>
                ))}
            </div>
            <div style={{display:"flex", flexDirection:"column"}}>
                <h2>Add Product</h2>
                <form style={{display:"flex", flexDirection:"column", textAlign:"center", alignItems:"center"}}>
                    <input onClick={handleSetProduct} type="text" placeholder="Product Name" />
                    <input type="number" placeholder="Product Stock" />
                    <select name="category">
                        {categories.map((category) => (
                        <option key={category.ct_id} value={category.ct_name}>
                            {category.ct_name}
                        </option>
                        ))}
                    </select>
                </form>
                    <button onClick={handleSubmit} >Add Product</button>
            </div>
        </div>
    );

};

export default Products;
